import Cookies from 'js-cookie'
const credMng = {
  credentialKeySave: (data, rmb = '') => (rmb ? localStorage.setItem('_jwtApiToken', data) : Cookies.set('_jwtApiToken', data)),
  credentialKeyTake: () => (Cookies.get('_jwtApiToken') ? Cookies.get('_jwtApiToken') : localStorage.getItem('_jwtApiToken')),
  credentialKeyRemove: () => {
    Cookies.remove('_jwtApiToken')
    localStorage.removeItem('_jwtApiToken')
  },
  axiosHeaderToken: () => {
    return {
      headers: {
        Authorization: `Bearer ${Cookies.get('_jwtApiToken') ? Cookies.get('_jwtApiToken') : localStorage.getItem('_jwtApiToken')}`
      }
    }
  }
}
export default credMng
