const ArrSearchKey = () => {
  for (let i = 0; i < array.length; i++) {
    if (array[i][key] === value) {
      return i
    }
  }
}

const responseArrayMessage = (data, key = '') => {
  let errorMsg = ''
  if (typeof data === 'object') {
    if (data[key]) {
      data[key].forEach(msg => {
        errorMsg += errorMsg ? `, ${msg}` : msg
      })
    }
  } else {
    errorMsg += data
  }
  return errorMsg
}

const ArrHelper = {
  getKeyByValue: (data, key, value) => ArrSearchKey(data, key, value),
  getMessage: (data, key = '') => responseArrayMessage(data, key)
}

export default ArrHelper
