// 
const urlParams = (url) => new URLSearchParams(url)

const getParamValue = (url, param) => urlParams(url).get(param)

const patchParamValue = (url, param, newValue) => {
    let urls = urlParams(url)
    urls.set(param, newValue)
    return decodeURIComponent(urls)
}

const deleteParamValue = (url, param) => {
    let urls = urlParams(url)
    urls.delete(param)
    return decodeURIComponent(urls)
}

var UrlHelper = {
    getUrlParamValue: (url, param) => getParamValue(url, param),
    updateUrlParamValue: (url, param, newValue) => patchParamValue(url, param, newValue),
    deleteUrlParamValue: (url, param) => deleteParamValue(url, param)
}

export default UrlHelper
