import $ from "jquery";
import "jquery.easing";
import awaitSleep from "@/libs/apps/helper/await-sleep.min.js";
var jQEasing = {
  async scrollEasing(gotoElement, mrTop = "") {
    await awaitSleep.sleep(1000);
    let elmAddr = $(gotoElement);
    $("html").animate(
      { scrollTop: elmAddr.offset().top - mrTop },
      1250,
      "easeInOutExpo"
    );
  }
};
export default jQEasing;
