import toastr from 'toastr'
toastr.options = { closeButton: !1, debug: !1, newestOnTop: !0, progressBar: !1, positionClass: 'toast-top-right', preventDuplicates: !1, onclick: null, showDuration: '300', hideDuration: '1000', timeOut: '5000', extendedTimeOut: '1000', showEasing: 'swing', hideEasing: 'linear', showMethod: 'fadeIn', hideMethod: 'fadeOut' }
